

package baitap1;

public class tinh {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int a = 30;
        int b = 7;
        double c = 4.5;
        
        int tong = a + b;
        double thuong = a/c;
        int phandu = a%(int)c;
        
        System.out.println("Tổng : "+ tong);
        System.out.println("Thương : "+ thuong);
        System.out.println("Phần dư : "+ phandu);
	}
       
}
